			, Fnd_Goods_Amount
			, Fnd_Goods_Price
			, Fnd_Goods_Img
			, Fnd_Goods_ImgPath
			, Fnd_Post_Number
			)
	VALUES
			(
			#{fndGoodsNumber}
			, #{fndGoodsName}
			, #{fndGoodsContent}
			, #{fndGoodsAmount}
			, #{fndGoodsPrice}
			, #{fndGoodsImg}
			, #{fndGoodsImgPath}
			, #{fndPostNumber}
			)
