mapper.insert:

	SELECT NVL(MAX(Fnd_Post_Number), 0) + 1
	FROM funding_Post

INSERT INTO funding_Post
		(
		Fnd_Post_Number
		, Fnd_Category
		, Fnd_Title
		, Fnd_Main_Img
		, Fnd_Main_ImgPath
		, Fnd_Views
		, Fnd_Status
		, Fnd_Current_Amount
		, Fnd_Total_Amount
		, Fnd_Participants
		, Member_Email
		)
VALUES( 
		#{fndPostNumber}
		, #{fndCategory}
		, #{fndTitle}
		, #{fndMainImg}
		, #{fndMainImgPath}
		, 0
		, #{fndStatus}
		, 0
		, 0
		, 0
		, #{memberEmail}
		)

mapper.update:

UPDATE Funding_Post
SET 
	Fnd_Target_Amount = #{fndTargetAmount}
  , Fnd_Start_Date = #{fndStartDate}
  , Fnd_End_Date = #{fndEndDate}
  , Fnd_Wdate = SYSDATE
  , Fnd_Status = #{fndStatus}
  , Fnd_Content = #{fndContent}
WHERE fnd_Post_Number = #{fndPostNumber}


UPDATE Funding_Post
SET
	  Fnd_Current_Amount = Fnd_Current_Amount + #{fndTotalPrice}
 	, Fnd_Total_Amount = Fnd_Total_Amount + #{fndTotalPrice}
 	, Fnd_Participants = Fnd_Participants + 1
WHERE Fnd_Post_Number = #{fndPostNumber}


	UPDATE Funding_Post
  	SET Fnd_views = Fnd_views + 1
  	WHERE fnd_Post_Number = #{fndPostNumber}
