mapper.insert:
INSERT INTO member (
					member_email
					, member_pwd
					, member_nm
					, member_zip
					, member_baseaddr
					, member_detaaddr
					, member_cntinfo
					, member_grd
					
					, member_bizno
					, member_comaccp
					
					)
VALUES (
					#{memberEmail}
					, #{memberPwd}
					, #{memberNm}
					, #{memberZip}
					, #{memberBaseaddr}
					, #{memberDetaaddr}
					, #{memberCntinfo}
					, #{memberGrd}
					
					, #{memberBizno}
					, #{memberComaccp}
					
					)

mapper.select:
SELECT COUNT(*)
FROM member
WHERE member_email = #{memberEmail}

mapper.update:
UPDATE member
SET member_pwd = #{memberPwd}
WHERE member_email = #{memberEmail}
