mapper.insert:
		
			SELECT NVL(MAX(push_id),0) + 1
			FROM push
		
		INSERT INTO push
						(
						  push_id
						, push_target
						, push_typ
						, push_cntn
						
						, push_url
						
						)
						
				VALUES (
						#{pushId}
					  , #{pushTarget}
					  , #{pushTyp}
					  , #{pushCntn}
					  
					  , #{pushUrl}
					  
					)

mapper.update:
UPDATE PUSH
SET push_chk = 'AG1'
WHERE push_id = #{pushId}
