mapper.insert:
		
			SELECT NVL(MAX(push_id),0) + 1
			FROM push
		
		INSERT INTO push
						(
						  push_id
						, push_target
						, push_typ
						, push_cntn
						
						, push_url
						
						)
						
				VALUES (
						#{pushId}
					  , #{memberEmail}
					  , #{pushTyp}
					  , #{pushCntn}
					  
					  , #{pushUrl}
					  
					)

mapper.select:
SELECT COUNT(*)
FROM push
WHERE push_target = #{memberEmail}
